hooks.command:
if [ -f .cc-metrics-fail.json ]; then tries=$(cat .cc-refactor-attempts 2>/dev/null || echo 0); next=$((tries+1)); echo $next > .cc-refactor-attempts; python - <<'PY'
import json,sys
j=json.load(open('.cc-metrics-fail.json'))
print('\nüõ†Ô∏è  PLAN DE REFACTOR (objetivo: CC‚â§15 y LOC‚â§300):')
for it in j:
  f=it.get('file'); cc=it.get('cc') or it.get('ccBand'); loc=it.get('loc');
  tips=[]
  if (isinstance(cc,int) and cc>15) or cc=='red': tips.append('reduce complejidad: extrae funciones y usa early-returns')
  if (isinstance(loc,int) and loc>300) or it.get('locBand')=='red': tips.append('divide en m√≥dulos/componentes y extrae helpers')
  if tips: print(f' - {f}: ' + '; '.join(tips))
PY
; if [ $next -ge 3 ]; then echo '‚ö†Ô∏è Intentos de refactor agotados (3). Dejando de fallar autom√°ticamente.'; rm -f .cc-metrics-fail.json .cc-refactor-attempts >/dev/null 2>&1 || true; fi; fi

echo 'Aplicando auto-formato (timeout: 30s)...'; files=$(python - <<'PY'
import sys,json
j=json.load(sys.stdin)
paths=set()
ti=j.get('tool_input') or {}
tr=j.get('tool_response') or {}
for k in ('file_path','path','filePath'):
    if isinstance(ti,dict) and ti.get(k): paths.add(ti[k])
    if isinstance(tr,dict) and tr.get(k): paths.add(tr[k])
for k in ('files','changes','editedFiles'):
    v=tr.get(k)
    if isinstance(v,list):
        for x in v:
            if isinstance(x,str): paths.add(x)
            elif isinstance(x,dict):
                for kk in ('file','path','filePath'):
                    if kk in x: paths.add(x[kk])
print('\n'.join(sorted(paths)))
PY
); 
if [ -z "$files" ]; then 
  changed_git=$(git status --porcelain -z 2>/dev/null | tr '\0' '\n' | awk '{print $2}'); 
  changed_sess=$( [ -f .cc-session-start ] && find . -type f -newer .cc-session-start 2>/dev/null | sed 's|^\./||' ); 
  files=$(printf "%s\n%s\n" "$changed_git" "$changed_sess" | sort -u)
fi;
# Excluir rutas irrelevantes (generados, dependencias, caches)
files=$(printf "%s\n" "$files" | grep -Ev '(^|/)(coverage|node_modules|\.venv|build|dist|\.git|\.cache|\.pytest_cache)(/|$)' | grep -Ev '\.(min\.js|min\.css)$' || true)
[ -z "$files" ] && exit 0
for f in $files; do 
  case "$f" in 
    *.py)
      [ -f .venv/bin/activate ] && . .venv/bin/activate; 
      ruff "$f" --fix || true; 
      black "$f" --quiet 2>/dev/null || true 
      ;;
    *.ts|*.tsx|*.js|*.jsx|*.cjs|*.mjs)
      if [ -f yarn.lock ]; then 
        yarn -s eslint "$f" --max-warnings=0 --fix || true; 
        yarn -s prettier --write "$f" || true; 
      else 
        npx eslint "$f" --max-warnings=0 --fix || true; 
        npx prettier --write "$f" || true; 
      fi 
      ;;
    *.css|*.scss|*.sass|*.less)
      if [ -f yarn.lock ]; then 
        yarn -s prettier --write "$f" || true; 
      else 
        npx prettier --write "$f" || true; 
      fi 
      ;;
    *.html|*.htm)
      if [ -f yarn.lock ]; then 
        yarn -s prettier --write "$f" || true; 
      else 
        npx prettier --write "$f" || true; 
      fi 
      ;;
    *.json|*.jsonc)
      if [ -f yarn.lock ]; then 
        yarn -s prettier --write "$f" || true; 
      else 
        npx prettier --write "$f" || true; 
      fi 
      ;;
    *.md|*.mdx)
      markdownlint-cli2 --fix "$f" || true; 
      if [ -f yarn.lock ]; then 
        yarn -s prettier --write "$f" || true; 
      else 
        npx prettier --write "$f" || true; 
      fi 
      ;;
    *.yml|*.yaml)
      if command -v yamlfix >/dev/null 2>&1; then yamlfix "$f" || true; else yamllint "$f" || true; fi; 
      if [ -f yarn.lock ]; then 
        yarn -s prettier --write "$f" || true; 
      else 
        npx prettier --write "$f" || true; 
      fi 
      ;;
    *.xml|*.svg)
      if [ -f yarn.lock ]; then 
        yarn -s prettier --write "$f" --parser xml || true; 
      else 
        npx prettier --write "$f" --parser xml || true; 
      fi 
      ;;
    *.toml)
      command -v taplo >/dev/null 2>&1 && taplo format "$f" || true 
      ;;
    *.sh|*.bash|*.zsh)
      scdiff=$(shellcheck -f diff "$f" 2>/dev/null || true); 
      [ -n "$scdiff" ] && printf "%s" "$scdiff" | patch -p0 --silent || true; 
      (command -v shfmt >/dev/null 2>&1 && shfmt -w -i 2 -ci -sr "$f") || ([ -x './bin/shfmt.exe' ] && ./bin/shfmt.exe -w -i 2 -ci -sr "$f") || true 
      ;;
  esac; 
done

echo 'Analizando metricas de calidad (timeout: 15s)...'; fail=0; out='['; sep=''; files=$(python - <<'PY'
import sys,json
j=json.load(sys.stdin)
paths=set()
ti=j.get('tool_input') or {}
tr=j.get('tool_response') or {}
for k in ('file_path','path','filePath'):
    if isinstance(ti,dict) and ti.get(k): paths.add(ti[k])
    if isinstance(tr,dict) and tr.get(k): paths.add(tr[k])
for k in ('files','changes','editedFiles'):
    v=tr.get(k)
    if isinstance(v,list):
        for x in v:
            if isinstance(x,str): paths.add(x)
            elif isinstance(x,dict):
                for kk in ('file','path','filePath'):
                    if kk in x: paths.add(x[kk])
print('\n'.join(sorted(paths)))
PY
); 
if [ -z "$files" ]; then 
  changed_git=$(git status --porcelain -z 2>/dev/null | tr '\0' '\n' | awk '{print $2}'); 
  changed_sess=$( [ -f .cc-session-start ] && find . -type f -newer .cc-session-start 2>/dev/null | sed 's|^\./||' ); 
  files=$(printf "%s\n%s\n" "$changed_git" "$changed_sess" | sort -u)
fi;
# Excluir rutas irrelevantes (generados, dependencias, caches)
files=$(printf "%s\n" "$files" | grep -Ev '(^|/)(coverage|node_modules|\.venv|build|dist|\.git|\.cache|\.pytest_cache)(/|$)' | grep -Ev '\.(min\.js|min\.css)$' || true)
[ -z "$files" ] && { echo '‚Äî Design Metrics ‚Äî (sin archivos relevantes)'; exit 0; }
for f in $files; do 
  case "$f" in 
    *.py)
      [ -f .venv/bin/activate ] && . .venv/bin/activate; 
      loc=$(wc -l < "$f" 2>/dev/null || echo 0); 
      if command -v radon >/dev/null 2>&1; then 
        maxcc=$(radon cc -s -j "$f" 2>/dev/null | python - <<'PY'
import json,sys
try:
  j=json.load(sys.stdin)
except Exception:
  print(0); sys.exit(0)
mx=0
for _,items in j.items():
  for it in items:
    try:
      mx=max(mx,int(it.get('complexity',0)))
    except Exception:
      pass
print(mx)
PY
); 
      else echo "[PY] $f | ‚ö†Ô∏è radon no disponible (pip install radon)"; maxcc=0; fi; 
      [ "$maxcc" -le 10 ] || cc_band='yellow'; [ "$maxcc" -le 15 ] || { cc_band='red'; }; [ -n "$cc_band" ] || cc_band='green'; 
      [ "$loc" -le 212 ] || loc_band='yellow'; [ "$loc" -le 300 ] || { loc_band='red'; }; [ -n "$loc_band" ] || loc_band='green'; 
      [ "$cc_band" = 'red' ] && fail=1; [ "$loc_band" = 'red' ] && fail=1; 
      out="$out$sep{\"lang\":\"py\",\"file\":\"$f\",\"cc\":$maxcc,\"ccBand\":\"$cc_band\",\"loc\":$loc,\"locBand\":\"$loc_band\"}"; sep="," 
      ;;
    *.ts|*.tsx|*.js|*.jsx|*.cjs|*.mjs)
      loc=$(wc -l < "$f" 2>/dev/null || echo 0); 
      if [ -f yarn.lock ]; then ycmd='yarn -s'; else ycmd='npx'; fi; 
      $ycmd eslint "$f" --no-error-on-unmatched-pattern --rule '{"complexity":["error",10]}' >/dev/null 2>&1; ec10=$?; 
      $ycmd eslint "$f" --no-error-on-unmatched-pattern --rule '{"complexity":["error",15]}' >/dev/null 2>&1; ec15=$?; 
      if [ $ec10 -eq 0 ]; then cc_band='green'; elif [ $ec15 -eq 0 ]; then cc_band='yellow'; else cc_band='red'; fi; 
      [ "$loc" -le 212 ] || loc_band='yellow'; [ "$loc" -le 300 ] || { loc_band='red'; }; [ -n "$loc_band" ] || loc_band='green'; 
      [ "$cc_band" = 'red' ] && fail=1; [ "$loc_band" = 'red' ] && fail=1; 
      out="$out$sep{\"lang\":\"js\",\"file\":\"$f\",\"ccBand\":\"$cc_band\",\"loc\":$loc,\"locBand\":\"$loc_band\"}"; sep="," 
      ;;
  esac; done; 
  out="$out]"; echo "$out" > .cc-metrics-fail.json; 
  if [ $fail -ne 0 ]; then 
    echo '‚ùå Design Metrics en ROJO (CC>15 o LOC>300). Si timeout, revisa .cc-metrics-fail.json para detalles.' 1>&2; 
    exit 2; 
  else rm -f .cc-metrics-fail.json >/dev/null 2>&1 || true; fi
